import layout from '@/components/layout-page.vue';
import { getSeat } from '@/api/request';
export default {
  components: {
    layout
  },

  data() {
    return {
      currentPage: 1,
      pagesize: 10,
      total: 10,
      tableData: [],
      active: 1,
      Visible: false,
      form: {
        index: '',
        stuRoomNumber: '',
        buildingNumber: '',
        classRoomNumber: '',
        openStatus: '',
        time: '',
        returnTime: []
      },
      open: true
    };
  },

  methods: {
    handleCurrentChange(page) {
      this.currentPage = page;
      this.query();
    },

    next() {
      if (this.active++ > 2) this.active = 0;
    },

    query() {
      let roomNum = this.$route.query.roomnum;
      getSeat(roomNum).then(res => {
        if (res) {
          this.tableData = res;

          for (let i in res) {
            if (res[i].isVacant) this.tableData[i].isVacant = "是";else this.tableData[i].isVacant = "否";
            if (res[i].hasOutlet) this.tableData[i].hasOutlet = "是";else this.tableData[i].hasOutlet = "否"; // console.log(this.tableData[i].openStatus)
          }

          this.total = res.length;
        }
      });
    },

    chooseSeat(val) {
      let roomNum = this.$route.query.roomnum;
      $router.replace('/book/seat/book?roomnum=' + roomNum + '&seatnum=' + val.seatNum);
    }

  },

  mounted() {
    this.query();
  }

};
