import Vue from 'vue'
import VueRouter from 'vue-router'
//3、使用路由插件
// 在vue中，使用使用vue的插件，都需要调用Vue.use()
Vue.use(VueRouter)
//4、创建路由规则数组
const routes = [
  {
    path: "/",
    name: "home",
    component: () => import('@/components/layout-page.vue')
  },
  {
    path: "/layout",
    name: "layout",
    component: () => import('@/components/layout-page.vue')
  },
]
//5、创建路由对象 -  传入规则
const router = new VueRouter({
  routes
})
//6、关联到vue实例
new Vue({
	router
})
