export default {
  name: "MyLogin",

  data() {
    // 用户名的校验方法
    let validateName = (rule, value, callback) => {
      if (!value) {
        return callback(new Error("请输入用户名"));
      } // 用户名以字母开头,长度在5-16之间,允许字母数字下划线


      const userNameRule = /^[a-zA-Z][a-zA-Z0-9_]{4,15}$/;

      if (userNameRule.test(value)) {
        this.$refs.ruleForm.validateField("checkPass");
        return callback();
      } else {
        return callback(new Error("字母开头,长度5-16之间,允许字母数字下划线"));
      }
    }; // 密码的校验方法


    let validatePass = (rule, value, callback) => {
      if (value === "") {
        return callback(new Error("请输入密码"));
      } // 密码以字母开头,长度在6-18之间,允许字母数字和下划线


      const passwordRule = /^[a-zA-Z]\w{5,17}$/;

      if (passwordRule.test(value)) {
        this.$refs.ruleForm.validateField("checkPass");
        return callback();
      } else {
        return callback(new Error("字母开头,长度6-18之间,允许字母数字和下划线"));
      }
    };

    return {
      LoginUser: {
        name: "",
        pass: ""
      },
      // 用户信息校验规则,validator(校验方法),trigger(触发方式),blur为在组件 Input 失去焦点时触发
      rules: {
        name: [{
          validator: validateName,
          trigger: "blur"
        }],
        pass: [{
          validator: validatePass,
          trigger: "blur"
        }]
      }
    };
  },

  computed: {
    // 获取vuex中的showLogin，控制登录组件是否显示
    isLogin: {
      get() {
        return window.localStorage.getItem('username');
      },

      set(val) {
        this.$refs["ruleForm"].resetFields();
        this.setShowLogin(val);
      }

    }
  },
  methods: {
    // ...mapActions(["setUser", "setShowLogin"]),
    Login() {
      // 通过element自定义表单校验规则，校验用户输入的用户信息
      this.$refs["ruleForm"].validate(valid => {
        //如果通过校验开始登录
        if (valid) {
          if (this.LoginUser.name === 'admin' && this.LoginUser.pass === 'admin123') {
            // 隐藏登录组件
            this.isLogin = false; // 登录信息存到本地

            let user = JSON.stringify({
              userName: "admin",
              user_id: 8614
            });
            localStorage.setItem("user", user); // 登录信息存到vuex
            // this.setUser({userName: "admin", user_id: 8614});
            // 弹出通知框提示登录成功信息

            this.notifySucceed('登陆成功');
          } else {
            // 清空输入框的校验状态
            this.$refs["ruleForm"].resetFields(); // 弹出通知框提示登录失败信息

            this.notifyError('用户名或密码错误');
          }
        } else {
          return false;
        }
      });
    }

  }
};
