import layout from '@/components/layout-page.vue';
import { getSeat } from '@/api/request';
export default {
  components: {
    layout
  },

  data() {
    return {
      currentPage: 1,
      pagesize: 10,
      total: 10,
      tableData: [],
      active: 1,
      Visible: false,
      form: {
        index: '',
        stuRoomNumber: '',
        buildingNumber: '',
        classRoomNumber: '',
        openStatus: '',
        time: '',
        returnTime: []
      },
      open: true
    };
  },

  methods: {
    handleCurrentChange(page) {
      this.currentPage = page;
      this.query();
    },

    next() {
      if (this.active++ > 3) this.active = 0;
    },

    query() {
      let roomNum = this.$route.query.roomnum;
      getSeat(roomNum).then(res => {
        if (res) {
          this.tableData = res;

          for (let i in res) {
            if (res[i].isVacant) {
              this.tableData[i].isVacant = "是";
              this.tableData[i].is_Vacant = 1;
            } else {
              this.tableData[i].isVacant = "否";
              this.tableData[i].is_Vacant = 0;
            }

            if (res[i].hasOutlet) this.tableData[i].hasOutlet = "是";else this.tableData[i].hasOutlet = "否"; // console.log(this.tableData[i].openStatus)
          } // this.is_Vacant = 


          this.total = res.length;
        }
      });
    },

    chooseSeat(val) {
      let roomNum = this.$route.query.roomnum;
      let st = this.$route.query.st;
      let h = st.split(":")[0];
      let m = st.split(":")[1];
      let start = h + ':' + m;
      let et = this.$route.query.et;
      let eh = et.split(":")[0];
      let em = et.split(":")[0];
      let end = eh + ':' + em;
      let bn = this.$route.query.bn;
      let crn = this.$route.query.crn;
      this.$router.replace('/book/seat/time?roomnum=' + roomNum + '&seatnum=' + val.seatNum + '&st=' + start + '&et=' + end + '&bn=' + bn + '&crn=' + crn);
    }

  },

  mounted() {
    this.query();
  }

};
