import Vue from 'vue'
import VueRouter from 'vue-router'
//3、使用路由插件
// 在vue中，使用使用vue的插件，都需要调用Vue.use()
Vue.use(VueRouter)
//4、创建路由规则数组
const routes = [
  {
    path: "/",
    name: "home",
    component: () => import('@/components/MyLogin.vue')
  },
  {
    path: "/layout",
    name: "layout",
    component: () => import('@/components/layout-page.vue')
  },
  // 登录
  {
    path: "/login",
    name: "login",
    component: () => import('@/components/MyLogin.vue')
  },
   // 任务管理
   {
    path: "/task",
    name: "task",
    component: () => import('@/views/task-management.vue')
  },
  // 账户管理
  {
    path: "/account",
    name: "account",
    component: () => import('@/views/account-management.vue')
  },
  // 管理员页面
  {
    path: "/administrator",
    name: "administrator",
    component: () => import('@/views/administrator-page.vue')
  },
  // 404 页面
  {
    path: '*',
    name: '/404',
    component: resolve => require(['@/components/404.vue'], resolve),
},
]
//5、创建路由对象 -  传入规则
const router = new VueRouter({
  routes
})
export default router
