import layout from '@/components/layout-page.vue';
import { getStudyRoom, renewStudyRoom, bookingCheck } from '@/api/request';
export default {
  components: {
    layout
  },

  data() {
    return {
      currentPage: 1,
      pagesize: 10,
      total: 10,
      tableData: [],
      Visible: false,
      bookingVal: '',
      bookingInfo: [],
      form: {
        index: '',
        stuRoomNumber: '',
        buildingNumber: '',
        classRoomNumber: '',
        openStatus: '',
        time: '',
        returnTime: []
      },
      open: true,
      status: ''
    };
  },

  methods: {
    handleCurrentChange(page) {
      this.currentPage = page;
      this.query();
    },

    query() {
      getStudyRoom().then(res => {
        if (res) {
          this.tableData = res; // console.log(this.tableData)
          // if (this.tableData.openStatus) {
          //     this.tableData.openStatus = "true"
          // }
          // else{
          //     this.tableData.openStatus = "false"
          // }

          this.total = res.length;
        }
      });
    },

    done(val) {
      const params = val.stuNum;
      bookingCheck(params).then(res => {
        if (res) {
          this.$message.success('签到成功');
          this.bookingInfo = res['预约信息'];
          localStorage.setItem('bookingInfo', JSON.stringify(this.bookingInfo));
        } else {
          this.$message.success('签到失败');
        }
      });
    },

    renewRoom(val) {
      // this.form.id = val.index
      this.form.stuRoomNumber = val.stuRoomNumber;
      this.form.buildingNumber = val.buildingNumber;
      this.form.classRoomNumber = val.classRoomNumber;
      if (val.openStatus) this.status = "是";else this.status = "否";
      console.log(this.status);
      this.form.openStatus = this.status;
      this.form.returnTime.push(val.startTime, val.endTime);
      this.Visible = true;
    },

    check() {
      if (this.bookingInfo.length === 0) {
        this.$message.info('请先进行签到或者预约,才能查看预约信息');
        return;
      }

      this.$router.push({
        name: 'account',
        params: {
          bookingInfo: this.bookingInfo
        }
      });
    },

    sure() {
      if (!this.form.time) {
        this.$message.info('请选择自习室开放时间和结束时间！');
        return;
      }

      if (this.form.openStatus == "是") this.open = true;else this.open = false;
      let data = {
        // id: this.form.index,
        stuRoomNumber: this.form.stuRoomNumber,
        buildingNumber: this.form.buildingNumber,
        classRoomNumber: this.form.classRoomNumber,
        openStatus: this.open,
        startTime: this.form.time[0],
        endTime: this.form.time[1]
      };
      renewStudyRoom(data).then(res => {
        console.log(data, res);
        this.Visible = false;
        this.form = {
          index: '',
          stuRoomNumber: '',
          buildingNumber: '',
          classRoomNumber: '',
          openStatus: '',
          time: '',
          returnTime: []
        }; // this.renewInfo = res['预约信息']
        // localStorage.setItem('bookingInfo', JSON.stringify(this.bookingInfo))

        this.$message.success('更新成功');
        getStudyRoom();
      }).catch(err => {
        console.log(err);
        this.$message.success('更新失败');
      });
    }

  },

  mounted() {
    this.query();
  }

};
