import layout from '@/components/layout-page.vue';
import Seat from "@/components/Seat";
import { getStudyRoom, renewStudyRoom, addStudyRoom, deleteStudyRoom } from '@/api/request';
export default {
  components: {
    layout,
    Seat
  },

  data() {
    return {
      currentPage: 1,
      pagesize: 10,
      total: 10,
      tableData: [],
      active: 1,
      Visible: false,
      seatRows: null,
      blankRows: [],
      addVisible: false,
      deleteVisible: false,
      bookingVal: '',
      bookingInfo: [],
      form: {
        index: '',
        stuRoomNumber: '',
        buildingNumber: '',
        classRoomNumber: '',
        openStatus: '',
        time: '',
        returnTime: []
      },
      open: true,
      status: ''
    };
  },

  props: {
    item: {
      type: Object
    },
    color: {
      type: String
    }
  },
  methods: {
    handleCurrentChange(page) {
      this.currentPage = page;
      this.query();
    },

    next() {
      if (this.active++ > 2) this.active = 0;
    },

    query() {
      getStudyRoom().then(res => {
        if (res) {
          this.tableData = res;

          for (let i in res) {
            if (res[i].openStatus) this.tableData[i].openStatus = "是";else this.tableData[i].openStatus = "否"; // console.log(this.tableData[i].openStatus)
          }

          this.total = res.length;
        }
      });
    },

    chooseRoom(val) {
      // val.stuRoomNumber
      next();
      $router.push('/book/seat');
    },

    getColor(state) {
      if (state === 0) return '#31af0a';else if (state === 1) {
        return '#808080';
      } else {
        return '#d05c43';
      }
    },

    seatClick(index) {
      if (this.seatRows[index].type === 1 || this.seatRows[index].state === 2 || this.seatRows[index].state === 1) {
        this.seatRows[index].show = false;
        return;
      }

      this.seatCurIndex = index;
    },

    sure() {
      if (this.form.openStatus == "是" && !this.form.time) {
        this.$message.info('请选择自习室开放时间和结束时间！');
        return;
      }

      if (!this.form.time) {
        this.form.time = [null, null]; //灰色
      }

      if (this.form.openStatus == "是" || this.form.openStatus == "true") this.open = true;else this.open = false;
      let data = {
        // id: this.form.index,
        stuRoomNumber: this.form.stuRoomNumber,
        buildingNumber: this.form.buildingNumber,
        classRoomNumber: this.form.classRoomNumber,
        openStatus: this.open,
        startTime: this.form.time[0],
        endTime: this.form.time[1]
      };
      renewStudyRoom(data).then(res => {
        this.Visible = false; // 刷新页面

        this.form = {
          index: '',
          stuRoomNumber: '',
          buildingNumber: '',
          classRoomNumber: '',
          openStatus: '',
          time: '',
          returnTime: []
        };
        this.$message.success('更新成功');
        getStudyRoom().then(res => {
          if (res) {
            this.tableData = res;

            for (let i in res) {
              if (res[i].openStatus) this.tableData[i].openStatus = "是";else this.tableData[i].openStatus = "否";
            }

            this.total = res.length;
          }
        });
      }).catch(err => {
        console.log(err);
        this.$message.success('更新失败');
      });
    },

    add() {
      this.addVisible = true;
    },

    addSure() {
      if (this.form.openStatus == "是" && !this.form.time) {
        this.$message.info('请选择自习室开放时间和结束时间！');
        return;
      }

      if (!this.form.time) {
        this.form.time = [null, null]; //灰色
      } // console.log(this.form.openStatus)


      if (this.form.openStatus == "是" || this.form.openStatus == "true") this.open = true;else this.open = false;
      let data = {
        // id: this.form.index,
        stuRoomNumber: this.form.stuRoomNumber,
        buildingNumber: this.form.buildingNumber,
        classRoomNumber: this.form.classRoomNumber,
        openStatus: this.open,
        startTime: this.form.time[0],
        endTime: this.form.time[1]
      };
      console.log(data);
      addStudyRoom(data).then(res => {
        this.addVisible = false; // 刷新页面

        this.form = {
          index: '',
          stuRoomNumber: '',
          buildingNumber: '',
          classRoomNumber: '',
          openStatus: '',
          time: '',
          returnTime: []
        };
        this.$message.success('更新成功');
        getStudyRoom().then(res => {
          if (res) {
            this.tableData = res;

            for (let i in res) {
              if (res[i].openStatus) this.tableData[i].openStatus = "是";else this.tableData[i].openStatus = "否";
            }

            this.total = res.length;
          }
        });
      }).catch(err => {
        console.log(err);
        this.$message.success('更新失败');
      });
    },

    deleteRoom(val) {
      this.form.stuRoomNumber = val.stuRoomNumber;
      this.deleteVisible = true;
    },

    deleteSure(data) {
      // console.log(data)
      deleteStudyRoom(data).then(res => {
        this.deleteVisible = false; // 刷新页面

        this.form = {
          index: '',
          stuRoomNumber: '',
          buildingNumber: '',
          classRoomNumber: '',
          openStatus: '',
          time: '',
          returnTime: []
        };
        this.$message.success('删除成功');
        getStudyRoom().then(res => {
          if (res) {
            this.tableData = res;

            for (let i in res) {
              if (res[i].openStatus) this.tableData[i].openStatus = "是";else this.tableData[i].openStatus = "否";
            }

            this.total = res.length;
          }
        });
      }).catch(err => {
        console.log(err);
        this.$message.success('删除失败');
      });
    }

  },

  mounted() {
    this.query();
  }

};
